mapper.select:
SELECT COUNT(*)
FROM moviecrawl_tb
WHERE title = #{title}
  AND production_year = #{year}
  AND genre = #{genre}
  AND duration = #{duration}
  AND rating = #{rating}
  AND color = #{color}
  AND production_company = #{productionCompany}
  AND distribution_company = #{distributionCompany}
  AND director = #{director}
  AND `cast` = #{cast}
  AND keywords = #{keywords}
  AND synopsis = #{synopsis}
  AND director_intention = #{directorIntention}
  AND screening_and_awards = #{screeningAndAwards}
  AND director_filmography = #{directorFilmography}
  AND image_url = #{imageUrl}
  AND photoUrls = #{photoUrls}
  AND screenplay = #{screenplay}
  AND production = #{production}
  AND producer = #{producer}
  AND filming = #{filming}
  AND lighting = #{lighting}
  AND art = #{art}
  AND editing = #{editing}
  AND recording = #{recording}
  AND sound = #{sound}
  AND music = #{music}
  AND clothes = #{clothes}
  AND dressing = #{dressing}
